    int v1,v2,total;
    String operation;
    public void calc(){
        if(operation == "+"){
            total = v1 + v2;
        }
        if(operation == "-"){
            total = v1 - v2;
        }
        if(operation == "*"){
            total = v1 * v2;
        }
        else{
            
        }

        result.setText(""+total);
        v1 = total;
        numOne = total +"";
        numTwo = null;
        operation =null;
        
    }
    private void getInputs(String sign,String num){
        //clean
        if(sign == null && num ==null){
            result.setText("0");
            opLog.setText("0");
            numOne = null;
            numTwo = null;
            operation = null;
            return;
        }
        //add numbers
        if(sign==null){
            //get first number
          if (operation==null) {
            if(numOne==null&& num!=null){
                numOne = num;
            }
            else{
                numOne += num;
            }
            v1 = Integer.parseInt(numOne);
            
          }
          //get second number
          if (operation!=null) {
            if(numTwo==null&& num!=null){
                numTwo = num;
            }
            else{
                numTwo += num;
            }
            v2 = Integer.parseInt(numTwo);
      
          }
        }
        if(sign!=null){
            if (numOne==null && sign == "-"){
                numOne = "-";
            }
            if(numOne !=null && numTwo == null && operation != "="){
                operation = sign;
            }
            if(numOne!=null&& numTwo != null && operation != null){
              calc();
            }
        }

        opLog.setText((numOne==null?"":numOne)  +(operation==null&&operation!="="?"":operation) +(numTwo==null?"":numTwo));
        //numOne = Integer.parseInt(opLog.getText());

    }
